mapper.select:
SELECT * FROM twitter_record

    is_deleted = FALSE

        record_time = #{recordTime}


        tw_user_id = #{twUserId}


        tw_display_id = #{tw_display_id}

mapper.update:
UPDATE twitter_record
update_date = CURRENT_TIMESTAMP, record_time = CURRENT_TIMESTAMP, total_tweet_count = #{totalTweetCount}, date_record_time = #{dateRecordTime}, total_tweet_count_at_date = #{totalTweetCountAtDate}, tw_display_id = #{twDisplayId}
tw_user_id = #{twUserId} AND is_deleted = FALSE

UPDATE twitter_record

    update_date = CURRENT_TIMESTAMP,
    date_record_time = #{dateRecordTime},
    total_tweet_count_at_date = #{totalTweetCountAtDate}


    tw_user_id = #{twUserId}
    AND is_deleted = FALSE
